import { SessionEvent } from "event-manager";
import { Session } from "session";

function applySideEffects(session: Session, toolName: string) {
  let cursor = "default";
  if (toolName === "draw") {
    cursor = "crosshair";
  }
  if (toolName !== "default") {
    session.currentStage.canvas.executeFunction("drawShape", "rect", cursor);
  }
}

export default function SelectToolGlue(session: Session, e: SessionEvent) {
  applySideEffects(session, e.payload.toolName);
}
