import { SessionEvent } from "event-manager";
import { Session } from "session";

export function applySideEffects(session: Session, key: string, value: any) {
  if (key === "editor.snap.snapMagnet") {
    session.currentStage.canvas.executeFunction("setSnapBound", value);
  }
}

export default function SetConfigGlue(session: Session, e: SessionEvent) {
  applySideEffects(session, e.payload.key, e.payload.value);
}
