import { SessionEvent } from "event-manager";
import { Session } from "session";

function applySideEffects(session: Session, type: "straight" | "disconnect") {
  if (type === "straight") {
    session.currentStage.canvas.executeFunction(
      "PathEditor.straightenSelectedPoint"
    );
  } else {
    session.currentStage.canvas.executeFunction(
      "PathEditor.disconnectSelectedPoint"
    );
  }
}

export default function SetPathGlue(session: Session, e: SessionEvent) {
  applySideEffects(session, e.payload.type);
}
