import { DomainLayer, LogicalExpression } from "domain-x";
import { Layer, PathEvent } from "@builderx-core/designer";
import { forEach, find } from "lodash";
import { SessionEvent } from "event-manager";
import { Session, StageArtboard, Mutators } from "session";
import { globalRefMap } from "global-ref-map";

function applySideEffects(session: Session, value: boolean) {
  if (value) {
    session.currentStage.canvas.executeFunction("StageDesignerRuler.show");
  } else {
    session.currentStage.canvas.executeFunction("StageDesignerRuler.hide");
  }
}

export default function SetRulerVisibilityGlue(
  session: Session,
  e: SessionEvent
) {
  applySideEffects(session, e.payload.value);
}
