import { SessionEvent } from "event-manager";
import { Session, GenerateLayerPreview } from "session";
import { DomainLayer } from "domain-x";

export function applySideEffects(session: Session, layers: Array<DomainLayer>) {
  GenerateLayerPreview(
    session,
    layers.map(l => l.getFullPath())
  );
  return;
}

export default function SetZoomGlue(session: Session, e: SessionEvent) {
  applySideEffects(session, e.payload.layers);
}
