import { SessionEvent } from "event-manager";
import { Session, StageArtboard, Mutators } from "session";

export function applySideEffects(session: Session, value: any) {
  session.currentStage.canvas.executeFunction("setSnapConfig", {
    snap: value
  });
}

export default function ToggleSnapGlue(session: Session, e: SessionEvent) {
  applySideEffects(session, e.payload.value);
}
