import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import SetConfig from "../Workspace/SetConfig";
export default class SetAppTutorialStep implements ICommand {
  displayName = "SetAppTutorialStep";
  session: Session;
  step: number;

  constructor(session: Session, step: number) {
    this.session = session;
    this.step = step;
  }
  execute() {
    this.session.commandManager.executeAndSkip(
      new SetConfig(this.session, "app.tutorial.currentStep", this.step)
    );
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
