import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import SetConfig from "../Workspace/SetConfig";
export default class SkipAppTutorial implements ICommand {
  displayName = "SkipAppTutorial";
  session: Session;
  constructor(session: Session) {
    this.session = session;
  }
  execute() {
    this.session.commandManager.executeAndSkip(
      new SetConfig(this.session, "app.tutorial.skipped", true)
    );
    this.session.drivers.analyticsService.addActivity(
      {
        name: `app.tutorial.skipped`,
        path: "",
        category: "app/command"
      },
      true
    );
  }

  undo() {
    //
  }
  redo() {
    //
  }
}
