import { File as DomainFile } from "domain-x";
import { Session } from "../../models/Session";
import createService from ".";

export const GetNextValidArtboard = createService(
  "GetNextValidArtboard",
  (session: Session) => {
    let nextArtboard: DomainFile | undefined;
    session.domain.files.forEach((file: DomainFile) => {
      if (!nextArtboard && !file.isSymbol()) {
        nextArtboard = file;
      }
    });
    return nextArtboard;
  }
);
