import { Session } from "../../models/Session";
import { File as DomainFile } from "domain-x";
import SelectFileService from "./SelectFile";
import selectRootLayer from "./SelectRootLayer";
import createService from ".";

const PreviewFile = createService(
  "PreviewFile",
  (session: Session, file: DomainFile) => {
    if (file) {
      SelectFileService(session, file.getFullPath());
      selectRootLayer(session);
    }
  }
);

export default PreviewFile;
