import { Session } from "../../models/Session";
import SetHoverLayers from "../../Mutators/SetHoverLayersMutator";
import { get } from "lodash";
import createService from ".";

const RemoveHoveredForAllLayers = createService(
  "RemoveHoveredForAllLayers",
  (session: Session) => {
    session.hoveredLayers = [];
    SetHoverLayers(session, get(session.selectedFile, "path"), []);
  }
);

export default RemoveHoveredForAllLayers;
