import { Session } from "../../models/Session";
import SetAllStyleAttributes from "../../Mutators/SetAllStyleAttributesMutator";
import createService from ".";

export const SetAllStyleService = createService(
  "SetAllStyleService",
  (
    session: Session,
    domainLayerPath: string,
    style: any,
    stylePropsName: any = "style"
  ) => {
    SetAllStyleAttributes(session, domainLayerPath, style, stylePropsName);
  }
);
