import { Session } from "../../models/Session";
import { File as DomainFile } from "domain-x";
import createService from ".";
import { SetFileDirtyMutator } from "../../Mutators";
const SetFileDirty = createService(
  "SetFileDirty",
  (
    session: Session,
    isDirty: boolean,
    file?: DomainFile | undefined,
    shouldEmitUpdates: boolean = true
  ) => {
    if (file) {
      SetFileDirtyMutator(session, file.path, isDirty);
    }
  }
);

export default SetFileDirty;
