import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import clearRecordingConditions from "../CommandServices/ClearRecordingConditions";
export default class ClearRecordingConditions implements ICommand {
  displayName = "ClearRecordingConditions";
  private session: Session;
  private shouldEmitUpdates?: boolean = true;

  constructor(session: Session, shouldEmitUpdates: boolean = true) {
    this.session = session;
    this.shouldEmitUpdates = shouldEmitUpdates;
  }
  execute() {
    clearRecordingConditions(this.session, this.shouldEmitUpdates);
  }
  undo() {
    // if (this.session.selectedFile) {
    //   this.session.selectedFile.emit("scopeVariables");
    // }
  }
  redo() {
    //
  }
}
