import { ICommand } from "command-manager";
import { Session } from "session";
export default class ProcessingFile implements ICommand {
  displayName = "ProcessingFile";
  private session: Session;
  private fileId: string;
  private flag: boolean;
  constructor(session: Session, fileId: string, flag: boolean) {
    this.session = session;
    this.fileId = fileId;
    this.flag = flag;
  }
  execute() {
    const file = this.session.domain.files.get(this.fileId);
    if (file) {
      file.setIsProcessing(this.flag);
      file.emit("isProcessing");
    }
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
