import { ICommand, CommandManager } from "command-manager";
import { Session } from "session";
import { Layer, DomainLayer } from "domain-x";

import { ILayerTemplate } from "common-interfaces";

import generateLayerPreviewService from "../CommandServices/GenerateLayerPreview";

export default class GenerateLayerPreview implements ICommand {
  private session: Session;
  private domainLayersPath: string[];
  displayName: string = "GenerateLayerPreview";

  constructor(session: Session, layers: Array<DomainLayer>) {
    this.session = session;
    this.domainLayersPath = layers.map(l => l.getFullPath());
  }
  execute() {
    generateLayerPreviewService(this.session, this.domainLayersPath);
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
