import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import { Layer as DomainLayer, DomainFile } from "domain-x";
import SetResizerVisibilityMutator from "../../Mutators/SetResizerVisibilityMutator";

export default class SetResizerVisibility implements ICommand {
  displayName = "SetResizerVisibility";
  private session: Session;
  private visibility: "show" | "hide";
  layer: DomainLayer | DomainFile;

  constructor(
    session: Session,
    layer: DomainLayer | DomainFile,
    visibility: "show" | "hide"
  ) {
    this.session = session;
    this.visibility = visibility;
    this.layer = layer;
  }

  execute() {
    SetResizerVisibilityMutator(
      this.session,
      this.layer.getFullPath(),
      this.visibility
    );
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
