import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import { action } from "model";
import SetFileCode from "./../CommandServices/SetFileCode";
export default class SetSmartFlex implements ICommand {
  displayName = "SetSmartFlex";
  session: Session;
  private value: boolean;
  constructor(session: Session, value: boolean) {
    this.session = session;
    this.value = value;
  }
  execute() {
    this.session.setSmartFlex(this.value);
    SetFileCode(this.session);
    action(() => {
      this.session.emit("smartFlex");
    });
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
