import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import notifyErrorService from "../CommandServices/NotifyError";

export default class NotifyError implements ICommand {
  displayName = "NotifyError";
  session: Session;
  message: string | string[];
  title: string;
  constructor(session: Session, message: string | string[], title: string) {
    this.session = session;
    this.message = message;
    this.title = title;
  }
  execute() {
    notifyErrorService(this.session, this.title, this.message);
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
