import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import notifyInfoService from "../CommandServices/NotifyInfo";

export default class NotifyInfo implements ICommand {
  displayName = "NotifyInfo";
  session: Session;
  message: string;
  title: string;
  constructor(session: Session, message: string, title: string) {
    this.session = session;
    this.message = message;
    this.title = title;
  }
  execute() {
    notifyInfoService(this.session, this.title, this.message);
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
