import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import notifySuccessService from "../CommandServices/NotifySuccess";

export default class NotifySuccess implements ICommand {
  displayName = "NotifySuccess";
  session: Session;
  message: string | string[];
  title: string;
  constructor(session: Session, message: string | string[], title: string) {
    this.session = session;
    this.message = message;
    this.title = title;
  }
  execute() {
    notifySuccessService(this.session, this.title, this.message);
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
