import { ICommand } from "command-manager";
import { Layer as DomainLayer } from "domain-x";
import { Session } from "../../../models/Session";
import SetLayerCollapseMutator from "src/session/lib/Mutators/SetLayerCollapseMutator";

export default class SetLayerCollapse implements ICommand {
  displayName = "SetLayerCollapse";
  session: Session;
  layerId: string;
  collapse: boolean;
  constructor(session: Session, layer: DomainLayer, collapse: boolean) {
    this.session = session;
    this.collapse = collapse;
    this.layerId = layer.getFullPath();
  }
  execute() {
    SetLayerCollapseMutator(this.session, this.layerId, this.collapse);
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
