import { ICommand } from "command-manager";
import { Session } from "./../../models/Session";

export default class SetModalRoute implements ICommand {
  displayName = "SetModalRoute";
  private session: Session;
  private route: string;
  private routeInfo: any;
  constructor(session: Session, route: string, routeInfo?: any) {
    this.session = session;
    this.route = route;
    this.routeInfo = routeInfo;
  }
  execute() {
    this.session.drivers.analyticsService.addActivity(
      {
        name: `set.modal.route`,
        path: "",
        category: "app/command"
      },
      true
    );
    this.session.modalRouter.setRoute(this.route);
    if (this.routeInfo) {
      this.session.modalRouter.setRouteInfo(this.routeInfo);
    }
    this.session.modalRouter.emit("currentRoute");
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
