import { ICommand } from "command-manager";
import { Session } from "./../../models/Session";

export default class SetPageRoute implements ICommand {
  displayName = "SetPageRoute";
  private session: Session;
  private route: string;
  constructor(session: Session, route: string) {
    this.session = session;
    this.route = route;
  }
  execute() {
    this.session.drivers.analyticsService.addActivity(
      {
        name: `set.page.route`,
        path: "",
        category: "app/command"
      },
      true
    );

    this.session.pageRouter.changeRoute(this.route);

    this.session.pageRouter.emit("currentRoute");
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
