import { ICommand } from "command-manager";
import { Session } from "../../models/Session";

export default class CloseCodeEditor implements ICommand {
  displayName = "CloseCodeEditor";
  session: Session;
  constructor(session: Session) {
    this.session = session;
  }
  execute() {
    this.session.showCodeEditor = false;
    this.session.emit("showCodeEditor");
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
