import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import SetZoom from "./SetZoom";
import CommandKeyMapperService from "../../services/CommandKeyMapperService";
import { get } from "lodash";

export default class DisplayEditButton implements ICommand {
  displayName = "DisplayEditButton";
  session: Session;
  value: boolean;
  constructor(session: Session, value: boolean) {
    this.session = session;
    this.value = value;
  }

  execute() {
    if (this.value && get(this.session.project, "access.access") !== "write") {
      return;
    }
    this.session.editButton = this.value;
    this.session.emit("displayEditButton");
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
