import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import { File as DomainFile } from "domain-x";
import FocusArtboardMutator from "../../Mutators/FocusArtboardMutator";

export default class FocusArtboard implements ICommand {
  displayName = "FocusArtboard";
  session: Session;
  file: DomainFile;
  constructor(session: Session, file: DomainFile) {
    this.session = session;
    this.file = file;
  }

  execute() {
    FocusArtboardMutator(this.session, this.file.path);
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
