import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import PreviewFile from "./PreviewFile";
import { GetNextArtboard } from "../CommandServices/GetArtboardForPreview";

export default class NextArtboardPreview implements ICommand {
  displayName = "NextArtboardPreview";
  session: Session;
  constructor(session: Session) {
    this.session = session;
  }
  execute() {
    const nextFile = GetNextArtboard(this.session);
    if (nextFile) {
      this.session.commandManager.executeAndSkip(
        new PreviewFile(this.session, nextFile)
      );
    }
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
