import { ICommand, CommandManager } from "command-manager";
import { Session } from "../../models/Session";
import PlaceArtboardsToCenterService from "../CommandServices/PlaceArtboardsToCenter";

export default class PlaceArtboardsToCenter implements ICommand {
  displayName = "PlaceArtboardsToCenter";
  session: Session;
  commandManager: CommandManager;
  setFileIsDirty: boolean;
  initial: boolean = false;
  constructor(
    session: Session,
    setFileIsDirty: boolean = true,
    initial: boolean = false
  ) {
    this.session = session;
    this.setFileIsDirty = setFileIsDirty;
    this.commandManager = new CommandManager();
    this.initial = initial;
  }
  execute() {
    PlaceArtboardsToCenterService(this.session, this.initial);
  }
  undo() {
    this.commandManager.undoAll();
  }
  redo() {
    this.commandManager.redoAll();
  }
}
