import { ICommand, CommandManager } from "command-manager";
import { Session } from "../../models/Session";
// import SetStyleAttribute from "../Style/SetStyleAttribute";
// import SetInitialScroll from "./SetInitialScroll";
import placeRulers from "../CommandServices/PlaceRuler";
export default class PlaceRulers implements ICommand {
  displayName = "PlaceRulers";
  session: Session;
  commandManager: CommandManager;
  constructor(session: Session) {
    this.session = session;
    this.commandManager = new CommandManager();
  }
  execute() {
    placeRulers(this.session);
  }
  undo() {
    // nope

    this.commandManager.undoAll();
  }
  redo() {
    // nope
    this.commandManager.redoAll();
  }
}
