import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import { File as DomainFile } from "domain-x";
import previewFile from "../CommandServices/PreviewFile";

export default class PreviewFile implements ICommand {
  displayName = "PreviewFile";
  session: Session;
  file: DomainFile;
  constructor(session: Session, file: DomainFile) {
    this.session = session;
    this.file = file;
  }
  execute() {
    previewFile(this.session, this.file);
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
