import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import PreviewFile from "./PreviewFile";
import { GetPreviousArtboard } from "../CommandServices/GetArtboardForPreview";

export default class PreviousArtboardPreview implements ICommand {
  displayName = "PreviousArtboardPreview";
  session: Session;
  constructor(session: Session) {
    this.session = session;
  }
  execute() {
    let prevFile = GetPreviousArtboard(this.session);
    // console.log(prevFile, "prev file here");
    if (prevFile) {
      this.session.commandManager.executeAndSkip(
        new PreviewFile(this.session, prevFile)
      );
    }
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
