import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import { Layer } from "domain-x";
import { remove } from "lodash";

export default class RemoveHoveredLayer implements ICommand {
  displayName = "RemoveHoveredLayer";
  session: Session;
  layer: Layer;
  constructor(session: Session, layer: Layer) {
    this.session = session;
    this.layer = layer;
  }
  execute() {
    this.layer.hovered = false;
    remove(this.session.hoveredLayers, this.layer);
    this.layer.emit("hovered");
    this.session.emit("hoveredLayers");
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
