import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import SetZoom from "./SetZoom";

export default class ZoomIn implements ICommand {
  displayName = "ZoomIn";
  session: Session;
  constructor(session: Session) {
    this.session = session;
  }

  execute() {
    const currentStage = this.session.currentStage;
    if (this.session.currentStage.tools.selectedTool !== "path") {
      this.session.commandManager.executeAndSkip(
        new SetZoom(this.session, this.session.calculateZoom())
      );
      currentStage.tools.emit("zoom");
    }
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
