import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import selectRootLayerService from "../CommandServices/SelectRootLayer";
import GenerateLayerPreview from "../CommandServices/GenerateLayerPreview";
export default class SelectRootLayer implements ICommand {
  displayName = "SelectRootLayer";
  session: Session;
  constructor(session: Session) {
    this.session = session;
  }
  execute() {
    selectRootLayerService(this.session);
    if (this.session.showCodeEditor) {
      GenerateLayerPreview(this.session, [
        this.session.currentRootLayer.getFullPath()
      ]);
    }
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
