import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import selectTool from "../CommandServices/SelectTool";

type toolType =
  | "default"
  | "view"
  | "ellipse"
  | "image"
  | "text"
  | "icon"
  | "path"
  | "switch"
  | "activityIndicator"
  | "slider"
  | "mapView"
  | "scrollView"
  | "touchableOpacity"
  | "textInput";
export default class SelectTool implements ICommand {
  displayName = "SelectTool";
  session: Session;
  toolName: toolType;
  abortDraw: boolean | undefined;
  constructor(session: Session, toolName: toolType, abortDraw?: boolean) {
    this.session = session;
    this.toolName = toolName;
    this.abortDraw = abortDraw;
  }
  execute() {
    selectTool(this.session, this.toolName, this.abortDraw);
    this.session.drivers.analyticsService.addActivity(
      {
        name: `tool.${this.toolName}.select`,
        path: "",
        category: "app/command"
      },
      true
    );
  }

  undo() {
    //
  }
  redo() {
    //
  }
}
