import { ICommand } from "command-manager";
import { Session } from "../../models/Session";

export default class SetCurrentScroll implements ICommand {
  displayName = "SetCurrentScroll";
  session: Session;
  scroll: {
    x: number;
    y: number;
  };
  constructor(
    session: Session,
    scroll: {
      x: number;
      y: number;
    }
  ) {
    this.session = session;
    this.scroll = scroll;
  }
  execute() {
    const currentStage = this.session.currentStage;
    currentStage.tools.currentScroll = this.scroll;
    this.session.emit("changeScroll");
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
