import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import { SetDrawTypeMutator } from "../../Mutators";

type toolType =
  | "default"
  | "view"
  | "ellipse"
  | "image"
  | "text"
  | "icon"
  | "path"
  | "switch"
  | "activityIndicator"
  | "slider"
  | "mapView"
  | "scrollView"
  | "touchableOpacity"
  | "textInput";
export default class SetDrawType implements ICommand {
  displayName = "SetDrawType";
  session: Session;
  type: toolType;
  constructor(session: Session, type: toolType) {
    this.session = session;
    this.type = type;
  }
  execute() {
    SetDrawTypeMutator(this.session, this.type);

    this.session.currentStage.tools.draw.emit("type");
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
