import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import { EditorLayout } from "common-interfaces";
export default class SetEditorLayout implements ICommand {
  displayName = "SetEditorLayout";
  session: Session;
  editorLayout: EditorLayout;
  constructor(session: Session, editorLayout: EditorLayout) {
    this.session = session;
    this.editorLayout = editorLayout;
  }
  execute() {
    this.session.editorLayout.setCurrent(this.editorLayout);
    this.session.emit("editorLayout");
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
