import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import setInitialScroll from "../CommandServices/SetInitialScroll";

export default class SetInitialScroll implements ICommand {
  displayName = "SetInitialScroll";
  session: Session;
  scroll: {
    x: number;
    y: number;
  };
  constructor(session: Session, scroll: { x: number; y: number }) {
    this.session = session;
    this.scroll = scroll;
  }
  execute() {
    setInitialScroll(this.session, this.scroll);
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
