import { ICommand } from "command-manager";
import { Session } from "../../models/Session";

export default class SetPathEditorData implements ICommand {
  displayName = "SetPathEditorData";
  session: Session;
  data: any;
  constructor(session: Session, data: any) {
    this.session = session;
    this.data = data;
  }
  execute() {
    this.session.currentStage.tools.pathEditorData = this.data;
    this.session.currentStage.tools.emit("pathEditorData");
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
