import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import { SetRulerVisibilityMutator } from "../../Mutators";

export default class SetRulerVisibility implements ICommand {
  displayName = "SetRulerVisibility";
  session: Session;
  value: boolean;
  constructor(session: Session, value: boolean) {
    this.session = session;
    this.value = value;
  }
  execute() {
    SetRulerVisibilityMutator(this.session, this.value);

    this.session.drivers.analyticsService.addActivity(
      {
        name: `ruler.toggle.${this.value}`,
        path: "",
        category: "app/command"
      },
      true
    );
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
