import { ICommand } from "command-manager";
import { Session } from "../../models/Session";

export default class SetZooming implements ICommand {
  displayName = "SetZooming";
  session: Session;
  zooming: boolean;

  constructor(session: Session, zooming: boolean) {
    this.session = session;
    this.zooming = zooming;
  }
  execute() {
    this.session.currentStage.tools.zooming = this.zooming;
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
