import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import SetFileCode from "./../CommandServices/SetFileCode";

export default class ToggleCodeEditor implements ICommand {
  displayName = "ToggleCodeEditor";
  session: Session;
  constructor(session: Session) {
    this.session = session;
  }
  execute() {
    const { showCodeEditor } = this.session;
    if (!showCodeEditor) {
      SetFileCode(this.session);
    }
    this.session.showCodeEditor = !showCodeEditor;
    this.session.emit("showCodeEditor");
  }
  undo() {
    // nope
  }
  redo() {
    // nope
  }
}
