import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import ToggleSnapMutator from "../../Mutators/ToggleSnapMutator";

export default class ToggleSnap implements ICommand {
  displayName = "ToggleSnap";
  session: Session;
  constructor(session: Session) {
    this.session = session;
  }
  execute() {
    let oldVal = this.session.drivers.configuration.get("editor.snap");
    ToggleSnapMutator(this.session, !oldVal);
    this.session.drivers.analyticsService.addActivity(
      {
        name: `snap.toggle.${!oldVal}`,
        path: "",
        category: "app/command"
      },
      true
    );
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
