function getRandomInt(length: number) {
  return Math.floor(Math.random() * length);
}

export function generateRandomName() {
  let name =
    upperFirst(name1[getRandomInt(name1.length + 1)]) +
    upperFirst(name2[getRandomInt(name2.length + 1)]);
  return name;
}
