import { ICommand } from "command-manager";
import { Session } from "../../models/Session";

export default class SetAppUpdaterIndicator implements ICommand {
  displayName = "SetAppUpdaterIndicator";
  private session: Session;
  private value: string;
  constructor(session: Session, value: string) {
    this.session = session;
    this.value = value;
  }
  execute() {
    this.session.drivers.analyticsService.addActivity(
      {
        name: `set.app.updater.indicator`,
        path: "",
        category: "app/command"
      },
      true
    );
    this.session.drivers.appUpdaterService.setAppUpdaterIndicator(this.value);
    this.session.emit("appUpdaterIndicator");
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
