import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
import SetConfigMutator from "../../Mutators/SetConfigMutator";

export default class SetConfig implements ICommand {
  displayName = "SetConfig";
  session: Session;
  key: string;
  value: boolean | string | number;
  constructor(session: Session, key: string, value: boolean | string | number) {
    this.session = session;
    this.key = key;
    this.value = value;
  }
  execute() {
    SetConfigMutator(this.session, this.key, this.value);
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
