import { ICommand } from "command-manager";
import { Session } from "../../models/Session";
export default class SetWarning implements ICommand {
  displayName = "SetWarning";
  private session: Session;
  private value: boolean;
  constructor(session: Session, value: boolean) {
    this.session = session;
    this.value = value;
  }
  execute() {
    this.session.setHasWarning(this.value);
    this.session.emit("hasWarning");
  }
  undo() {
    //
  }
  redo() {
    //
  }
}
