import { Session } from "../models/Session";

export default function AddStageArtboardMutator(
  session: Session,
  stageId: string,
  filePath: string,
  rootLayerId: string,
  thumbnailStage: boolean = false
) {
  const stagePath = !thumbnailStage ? "stages" : "thumbnailStages";

  const stageArtboard = session.emitPatch({
    op: "add",
    path: `/${stagePath}:::${stageId}:::/stageArtboards/-`,
    value: {
      type: "class",
      className: "StageArtboard",
      rootLayerId,
      filePath
    }
  });

  return stageArtboard;
}
